var notepadApp = angular.module('notepadApp', []);

(function () {
    'use strict';
    notepadApp.run(['ApplicationsFactory', function (ApplicationsFactory) {

        ApplicationsFactory.registerApplication({
            id: 'notepad',
            ico: 'pencil',
            name: 'Notepad',
            menu: true,
            style: 'width:600px;height:300px;top:10%;left:30%;'
        });

    }]);
}());

(function () {
    'use strict';
    notepadApp.controller('notepadBodyController', ['$scope', function ($scope) {

        var _this = this;
        this.editorData = 'test';

        require.config({
            baseUrl: 'https://microsoft.github.io/monaco-editor/node_modules/monaco-editor/min/'
        });

        var editor = monaco.editor.create(document.getElementById('notepad_editor'), {
            value: [
                'function x() {',
                '\tconsole.log("Hello world!");',
                '}'
            ].join('\n'),
            language: 'javascript',
            automaticLayout: true
        });

        console.log(editor);
        editor.layout();

        $scope.$on('notepad__new_data', function (event, data) {
            console.log('newdata');
            _this.editorData = data;
        });

    }]);
}());

(function () {
    'use strict';
    notepadApp.run(['$templateCache', function ($templateCache) {

        $templateCache.put('templates/applications/body-notepad.html',
            '<div class="window__body" ng-controller="notepadBodyController as nB"> \
              <div class="window__main no_padding" id="notepad_editor"> \
              </div> \
            </div>'
        );

    }]);
}());

(function () {
    'use strict';
    notepadApp.run(['$templateCache', function ($templateCache) {

        $templateCache.put('templates/applications/menu-notepad.html',
            '<li> \
          <a > \
            <i class="menu__icon fa fa-download"></i> \
            Save \
          </a> \
        </li> \
        <li> \
          <a > \
            <i class="menu__icon fa fa-folder-open"></i> \
            Open \
          </a> \
        </li> \
        <li> \
          <a > \
            <i class="menu__icon fa fa-print"></i> \
            Print \
          </a> \
        </li> \
        <li> \
          <a > \
            <i class="menu__icon fa fa-share-alt"></i> \
            Share \
          </a> \
        </li> \
        <li class="divided"> \
          <a > \
            <i class="menu__icon fa fa-file"></i> \
            Format \
          </a> \
        </li> \
        <li> \
          <a > \
            <i class="menu__icon fa fa-cog"></i> \
            Settings \
          </a> \
        </li>'
        );

    }]);
}());
